--------------------------------------------------------
--  DDL for Table COUNTRIES
--------------------------------------------------------
CREATE TABLE COUNTRIES(
	COUNTRY_ID		VARCHAR2(2) NOT NULL, 
	COUNTRY_NAME	VARCHAR2(40) NOT NULL, 
	REGION_ID		NUMBER NOT NULL, 
	CONSTRAINT COUNTRY_ID_PK PRIMARY KEY (COUNTRY_ID) ENABLE
);

--------------------------------------------------------
--  DDL for Table DEPARTMENTS
--------------------------------------------------------
CREATE TABLE DEPARTMENTS(
	DEPARTMENT_ID	NUMBER(4,0), 
	DEPARTMENT_NAME	VARCHAR2(30), 
	MANAGER_ID		NUMBER(6,0), 
	LOCATION_ID		NUMBER(4,0),
	CONSTRAINT DEPT_ID_PK PRIMARY KEY (DEPARTMENT_ID) ENABLE
);

--------------------------------------------------------
--  DDL for Table EMPLOYEES
--------------------------------------------------------
CREATE TABLE EMPLOYEES(
	EMPLOYEE_ID NUMBER(6,0) NOT NULL, 
	FIRST_NAME VARCHAR2(20) NOT NULL, 
	LAST_NAME VARCHAR2(25) NOT NULL, 
	EMAIL VARCHAR2(25), 
	PHONE_NUMBER VARCHAR2(20), 
	HIRE_DATE DATE, 
	JOB_ID VARCHAR2(10) NOT NULL, 
	SALARY NUMBER(8,2), 
	COMMISSION_PCT NUMBER(2,2), 
	MANAGER_ID NUMBER(6,0), 
	DEPARTMENT_ID NUMBER(4,0),
	CONSTRAINT EMPLOYEE_ID_PK PRIMARY KEY (EMPLOYEE_ID) ENABLE,
	CONSTRAINT EMP_EMAIL_UK UNIQUE (EMAIL),
	CONSTRAINT EMP_SALARY_MIN CHECK (salary > 0) ENABLE	
);

--------------------------------------------------------
--  DDL for Table JOB_HISTORY
--------------------------------------------------------
CREATE TABLE JOB_HISTORY(
	EMPLOYEE_ID NUMBER(6,0) NOT NULL, 
	START_DATE DATE NOT NULL, 
	END_DATE DATE, 
	JOB_ID VARCHAR2(10) NOT NULL, 
	DEPARTMENT_ID NUMBER(4,0),
	CONSTRAINT JHISTORY_ID_PK PRIMARY KEY (EMPLOYEE_ID, START_DATE) ENABLE		
);

--------------------------------------------------------
--  DDL for Table JOBS
--------------------------------------------------------
CREATE TABLE JOBS(
	JOB_ID VARCHAR2(10) NOT NULL, 
	JOB_TITLE VARCHAR2(35) NOT NULL, 
	MIN_SALARY NUMBER(6,0), 
	MAX_SALARY NUMBER(6,0),
	CONSTRAINT JOB_ID_PK PRIMARY KEY (JOB_ID) ENABLE
);

--------------------------------------------------------
--  DDL for Table LOCATIONS
--------------------------------------------------------
CREATE TABLE LOCATIONS(
	LOCATION_ID NUMBER(4,0) NOT NULL, 
	STREET_ADDRESS VARCHAR2(40), 
	POSTAL_CODE VARCHAR2(12), 
	CITY VARCHAR2(30), 
	STATE_PROVINCE VARCHAR2(25), 
	COUNTRY_ID VARCHAR2(2),
	CONSTRAINT LOCATION_ID_PK PRIMARY KEY (LOCATION_ID) ENABLE
);

--------------------------------------------------------
--  DDL for Table REGIONS
--------------------------------------------------------
CREATE TABLE REGIONS(
	REGION_ID NUMBER NOT NULL, 
	REGION_NAME VARCHAR2(25) NOT NULL,
	CONSTRAINT REGION_ID_PK PRIMARY KEY (REGION_ID) ENABLE
);

