--  DDL for View EMP_DETAILS_VIEW
--------------------------------------------------------
CREATE OR REPLACE FORCE VIEW EMP_DETAILS_VIEW (
	employee_id, job_id, manager_id, department_id, location_id, 
	country_id, first_name, last_name, salary, commission_pct, department_name, job_title, city, 
	state_province, country_name, region_name ) AS 
SELECT
	e.employee_id,
	e.job_id,
	e.manager_id,
	e.department_id,
	d.location_id,
	l.country_id,
	e.first_name,
	e.last_name,
	e.salary,
	e.commission_pct,
	d.department_name,
	j.job_title,
	l.city,
	l.state_province,
	c.country_name,
	r.region_name
FROM employees e, departments d, jobs j, locations l, countries c, regions r
WHERE e.department_id = d.department_id
AND   d.location_id   = l.location_id
AND   l.country_id    = c.country_id
AND   c.region_id     = r.region_id
AND   j.job_id        = e.job_id;


--------------------------------------------------------
--  DDL for Trigger UPDATE_JOB_HISTORY
--------------------------------------------------------
CREATE OR REPLACE TRIGGER UPDATE_JOB_HISTORY 
	AFTER UPDATE OF job_id, department_id
	ON employees
	FOR EACH ROW
BEGIN
	INSERT INTO job_history (employee_id, start_date, end_date, job_id, department_id)
	VALUES( :OLD.employee_id, :OLD.hire_date, SYSDATE, :OLD.job_id, :OLD.department_id);
END;
/
ALTER TRIGGER UPDATE_JOB_HISTORY ENABLE;


